    describe("Profiling tests", function () {
        beforeEach(function (done) {
            // start with empty form to reduce effect of previous tests
            util.init({
                core: {
                    onReady: function () {
                        done();
                    }
                }
            });
        });

        for (var i = 0; i < 6; i++) {
            it("should load large form " + i, function (done) {
                util.init({
                    core: {
                        form: LARGE_XFORM,
                        onReady: function () {
                            done();
                        }
                    }
                });
            });
        }

        it("should load huge form ", function (done) {
            // measured time was ~1.5 minutes, this is artificially high
            this.timeout(5 * 60 * 1000);

            util.init({
                core: {
                    form: HUGE_XFORM,
                    onReady: function () {
                        done();
                    }
                }
            });
        });
    });

